import{_ as e,c as n,o as t,a as s}from"./app.419fa203.js";const y=JSON.parse('{"title":"Documentation des Syst\xE8mes ECS","description":"","frontmatter":{},"headers":[{"level":2,"title":"ShootSystem","slug":"shootsystem","link":"#shootsystem","children":[{"level":3,"title":"R\xF4le","slug":"role","link":"#role","children":[]},{"level":3,"title":"Fonctionnement","slug":"fonctionnement","link":"#fonctionnement","children":[]},{"level":3,"title":"R\xE9sum\xE9","slug":"resume","link":"#resume","children":[]}]},{"level":2,"title":"SpawnSystem","slug":"spawnsystem","link":"#spawnsystem","children":[{"level":3,"title":"R\xF4le","slug":"role-1","link":"#role-1","children":[]},{"level":3,"title":"Fonctionnement","slug":"fonctionnement-1","link":"#fonctionnement-1","children":[]},{"level":3,"title":"R\xE9sum\xE9","slug":"resume-1","link":"#resume-1","children":[]}]},{"level":2,"title":"DestroySystem","slug":"destroysystem","link":"#destroysystem","children":[{"level":3,"title":"R\xF4le","slug":"role-2","link":"#role-2","children":[]},{"level":3,"title":"Fonctionnement","slug":"fonctionnement-2","link":"#fonctionnement-2","children":[]},{"level":3,"title":"R\xE9sum\xE9","slug":"resume-2","link":"#resume-2","children":[]}]},{"level":2,"title":"ColliderSystem","slug":"collidersystem","link":"#collidersystem","children":[{"level":3,"title":"R\xF4le","slug":"role-3","link":"#role-3","children":[]},{"level":3,"title":"Fonctionnement","slug":"fonctionnement-3","link":"#fonctionnement-3","children":[]},{"level":3,"title":"R\xE9sum\xE9","slug":"resume-3","link":"#resume-3","children":[]}]},{"level":2,"title":"RenderSystem","slug":"rendersystem","link":"#rendersystem","children":[{"level":3,"title":"R\xF4le","slug":"role-4","link":"#role-4","children":[]},{"level":3,"title":"Fonctionnement","slug":"fonctionnement-4","link":"#fonctionnement-4","children":[]},{"level":3,"title":"R\xE9sum\xE9","slug":"resume-4","link":"#resume-4","children":[]}]},{"level":2,"title":"InputSystem","slug":"inputsystem","link":"#inputsystem","children":[{"level":3,"title":"R\xF4le","slug":"role-5","link":"#role-5","children":[]},{"level":3,"title":"Fonctionnement","slug":"fonctionnement-5","link":"#fonctionnement-5","children":[]},{"level":3,"title":"R\xE9sum\xE9","slug":"resume-5","link":"#resume-5","children":[]}]},{"level":2,"title":"PhysicsSystem","slug":"physicssystem","link":"#physicssystem","children":[{"level":3,"title":"R\xF4le","slug":"role-6","link":"#role-6","children":[]},{"level":3,"title":"Fonctionnement","slug":"fonctionnement-6","link":"#fonctionnement-6","children":[]}]}],"relativePath":"R-type_Part1/ECS/System/index.md"}'),o={name:"R-type_Part1/ECS/System/index.md"},i=s(`<h1 id="documentation-des-systemes-ecs" tabindex="-1">Documentation des Syst\xE8mes ECS <a class="header-anchor" href="#documentation-des-systemes-ecs" aria-hidden="true">#</a></h1><p>Ce document pr\xE9sente une vue d\u2019ensemble des diff\xE9rents syst\xE8mes (<code>Systems</code>) utilis\xE9s dans ton architecture <strong>Entity Component System (ECS)</strong>.<br> Chaque syst\xE8me a une responsabilit\xE9 pr\xE9cise et agit sur un ensemble de composants sp\xE9cifiques.<br> Ensemble, ils forment le c\u0153ur de la boucle de gameplay du jeu.</p><hr><h2 id="shootsystem" tabindex="-1">ShootSystem <a class="header-anchor" href="#shootsystem" aria-hidden="true">#</a></h2><h3 id="role" tabindex="-1">R\xF4le <a class="header-anchor" href="#role" aria-hidden="true">#</a></h3><p>Le <strong>ShootSystem</strong> est responsable de la <strong>cr\xE9ation et de la gestion des projectiles</strong> tir\xE9s par les entit\xE9s capables de tirer (joueur, ennemi, tourelle, etc.).</p><h3 id="fonctionnement" tabindex="-1">Fonctionnement <a class="header-anchor" href="#fonctionnement" aria-hidden="true">#</a></h3><ul><li>Identifie les entit\xE9s poss\xE9dant les composants : <ul><li><code>PositionComponent</code></li><li><code>RectComponent</code></li><li><code>MvtComponent</code></li><li><code>ShootComponent</code></li></ul></li><li>Pour chaque entit\xE9 trouv\xE9e, il cr\xE9e un <strong>nouveau projectile</strong> (nouvelle entit\xE9).</li><li>Ce projectile re\xE7oit ses propres composants : position, texture, vitesse, d\xE9g\xE2ts, etc.</li></ul><h3 id="resume" tabindex="-1">R\xE9sum\xE9 <a class="header-anchor" href="#resume" aria-hidden="true">#</a></h3><blockquote><p>Produit les tirs et ajoute les projectiles dans le monde pour \xEAtre trait\xE9s par les autres syst\xE8mes.</p></blockquote><hr><h2 id="spawnsystem" tabindex="-1">SpawnSystem <a class="header-anchor" href="#spawnsystem" aria-hidden="true">#</a></h2><h3 id="role-1" tabindex="-1">R\xF4le <a class="header-anchor" href="#role-1" aria-hidden="true">#</a></h3><p>Le <strong>SpawnSystem</strong> g\xE8re la <strong>cr\xE9ation dynamique d\u2019ennemis ou d\u2019objets IA</strong> \xE0 intervalles r\xE9guliers ou al\xE9atoires.</p><h3 id="fonctionnement-1" tabindex="-1">Fonctionnement <a class="header-anchor" href="#fonctionnement-1" aria-hidden="true">#</a></h3><ul><li>G\xE9n\xE8re une <strong>position al\xE9atoire</strong> pour chaque nouvel ennemi.</li><li>Cr\xE9e une nouvelle entit\xE9 dans le <code>registry</code>.</li><li>Attribue les composants suivants : <ul><li><code>PositionComponent</code></li><li><code>DestroyableComponent</code></li><li><code>ShootComponent</code></li><li><code>ComputerComponent</code></li><li><code>TextureComponent</code></li><li><code>LifeComponent</code></li><li><code>RectComponent</code></li></ul></li></ul><h3 id="resume-1" tabindex="-1">R\xE9sum\xE9 <a class="header-anchor" href="#resume-1" aria-hidden="true">#</a></h3><blockquote><p>Fait appara\xEEtre de nouvelles entit\xE9s ennemies dans la sc\xE8ne de jeu.</p></blockquote><hr><h2 id="destroysystem" tabindex="-1">DestroySystem <a class="header-anchor" href="#destroysystem" aria-hidden="true">#</a></h2><h3 id="role-2" tabindex="-1">R\xF4le <a class="header-anchor" href="#role-2" aria-hidden="true">#</a></h3><p>Le <strong>DestroySystem</strong> g\xE8re la <strong>suppression des entit\xE9s</strong> apr\xE8s une collision ou lorsqu\u2019elles n\u2019ont plus de points de vie.</p><h3 id="fonctionnement-2" tabindex="-1">Fonctionnement <a class="header-anchor" href="#fonctionnement-2" aria-hidden="true">#</a></h3><ul><li>Parcourt les entit\xE9s avec : <ul><li><code>LifeComponent</code></li><li><code>FireComponent</code></li><li><code>ComputerComponent</code></li><li><code>PositionComponent</code></li></ul></li><li>D\xE9tecte les collisions simples (par comparaison de positions).</li><li>R\xE9duit les points de vie (<code>LifeComponent</code>) des entit\xE9s touch\xE9es.</li><li>Supprime (<code>kill_entity()</code>) les entit\xE9s mortes ou d\xE9truites.</li></ul><h3 id="resume-2" tabindex="-1">R\xE9sum\xE9 <a class="header-anchor" href="#resume-2" aria-hidden="true">#</a></h3><blockquote><p>Supprime les entit\xE9s hors-jeu ou d\xE9truites apr\xE8s un impact.</p></blockquote><hr><h2 id="collidersystem" tabindex="-1">ColliderSystem <a class="header-anchor" href="#collidersystem" aria-hidden="true">#</a></h2><h3 id="role-3" tabindex="-1">R\xF4le <a class="header-anchor" href="#role-3" aria-hidden="true">#</a></h3><p>Le <strong>ColliderSystem</strong> d\xE9tecte les <strong>collisions physiques</strong> entre les entit\xE9s et communique ces informations aux autres syst\xE8mes (notamment <code>DestroySystem</code> et <code>PhysicsSystem</code>).</p><h3 id="fonctionnement-3" tabindex="-1">Fonctionnement <a class="header-anchor" href="#fonctionnement-3" aria-hidden="true">#</a></h3><ul><li>V\xE9rifie si deux entit\xE9s se recoupent \xE0 partir de : <ul><li><code>PositionComponent</code></li><li><code>RectComponent</code></li></ul></li><li>Peut retourner une liste de collisions ou directement d\xE9clencher des r\xE9actions.</li></ul><h3 id="resume-3" tabindex="-1">R\xE9sum\xE9 <a class="header-anchor" href="#resume-3" aria-hidden="true">#</a></h3><blockquote><p>D\xE9tecte les collisions et sert de base aux r\xE9actions physiques ou destructives.</p></blockquote><hr><h2 id="rendersystem" tabindex="-1">RenderSystem <a class="header-anchor" href="#rendersystem" aria-hidden="true">#</a></h2><h3 id="role-4" tabindex="-1">R\xF4le <a class="header-anchor" href="#role-4" aria-hidden="true">#</a></h3><p>Le <strong>RenderSystem</strong> est charg\xE9 de <strong>l\u2019affichage visuel</strong> du jeu.<br> Il rend \xE0 l\u2019\xE9cran toutes les entit\xE9s poss\xE9dant une position et une texture.</p><h3 id="fonctionnement-4" tabindex="-1">Fonctionnement <a class="header-anchor" href="#fonctionnement-4" aria-hidden="true">#</a></h3><ul><li>R\xE9cup\xE8re les entit\xE9s avec : <ul><li><code>PositionComponent</code></li><li><code>TextureComponent</code></li><li>(optionnellement <code>RectComponent</code> pour la taille)</li></ul></li><li>Dessine chaque entit\xE9 \xE0 sa position courante.</li><li>G\xE8re les calques ou la profondeur d\u2019affichage si n\xE9cessaire.</li></ul><h3 id="resume-4" tabindex="-1">R\xE9sum\xE9 <a class="header-anchor" href="#resume-4" aria-hidden="true">#</a></h3><blockquote><p>Assure l\u2019affichage \xE0 l\u2019\xE9cran de toutes les entit\xE9s visibles.</p></blockquote><hr><h2 id="inputsystem" tabindex="-1">InputSystem <a class="header-anchor" href="#inputsystem" aria-hidden="true">#</a></h2><h3 id="role-5" tabindex="-1">R\xF4le <a class="header-anchor" href="#role-5" aria-hidden="true">#</a></h3><p>Le <strong>InputSystem</strong> g\xE8re les <strong>entr\xE9es utilisateur</strong> (clavier, souris, manette).<br> Il traduit les actions du joueur en mises \xE0 jour sur les composants de l\u2019entit\xE9 contr\xF4l\xE9e.</p><h3 id="fonctionnement-5" tabindex="-1">Fonctionnement <a class="header-anchor" href="#fonctionnement-5" aria-hidden="true">#</a></h3><ul><li>Lit l\u2019\xE9tat des touches ou des boutons.</li><li>Met \xE0 jour les composants : <ul><li><code>VelocityComponent</code> \u2192 d\xE9placement du joueur</li><li><code>ShootComponent</code> \u2192 tir</li><li><code>ActionComponent</code> \u2192 interactions</li></ul></li><li>Permet au joueur de d\xE9placer, tirer ou interagir.</li></ul><h3 id="resume-5" tabindex="-1">R\xE9sum\xE9 <a class="header-anchor" href="#resume-5" aria-hidden="true">#</a></h3><blockquote><p>Transforme les entr\xE9es utilisateur en actions sur les entit\xE9s.</p></blockquote><hr><h2 id="physicssystem" tabindex="-1">PhysicsSystem <a class="header-anchor" href="#physicssystem" aria-hidden="true">#</a></h2><h3 id="role-6" tabindex="-1">R\xF4le <a class="header-anchor" href="#role-6" aria-hidden="true">#</a></h3><p>Le <strong>PhysicsSystem</strong> met \xE0 jour la <strong>position et la vitesse</strong> des entit\xE9s selon les lois de la physique du jeu.</p><h3 id="fonctionnement-6" tabindex="-1">Fonctionnement <a class="header-anchor" href="#fonctionnement-6" aria-hidden="true">#</a></h3><ul><li>Parcourt les entit\xE9s avec : <ul><li><code>PositionComponent</code></li><li><code>MvtComponent</code> ou <code>VelocityComponent</code></li></ul></li><li>Met \xE0 jour les coordonn\xE9es :<div class="language-cpp line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">cpp</span><pre class="shiki vp-code-dark"><code><span class="line"><span style="color:#C9D1D9;">position.x </span><span style="color:#FF7B72;">+=</span><span style="color:#C9D1D9;"> velocity.vx;</span></span>
<span class="line"><span style="color:#C9D1D9;">position.y </span><span style="color:#FF7B72;">+=</span><span style="color:#C9D1D9;"> velocity.vy;</span></span>
<span class="line"></span></code></pre><pre class="shiki vp-code-light"><code><span class="line"><span style="color:#24292F;">position.x </span><span style="color:#CF222E;">+=</span><span style="color:#24292F;"> velocity.vx;</span></span>
<span class="line"><span style="color:#24292F;">position.y </span><span style="color:#CF222E;">+=</span><span style="color:#24292F;"> velocity.vy;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div></li></ul>`,56),l=[i];function r(a,c,d,u,h,m){return t(),n("div",null,l)}const f=e(o,[["render",r]]);export{y as __pageData,f as default};
